import { useEffect, useState } from "react";
import api from "../services/api";
import Navbar from "../components/Navbar";
import "./Queue.css";

export default function Queue() {
  const [name, setName] = useState("");
  const [serviceType, setServiceType] = useState("GENERAL");
  const [queue, setQueue] = useState([]);
  const [search, setSearch] = useState("");
  const [dark, setDark] = useState(
    localStorage.getItem("dark") === "true"
  );

  /* ================= DARK MODE ================= */
  useEffect(() => {
    document.documentElement.classList.toggle("dark", dark);
    localStorage.setItem("dark", dark);
  }, [dark]);

  /* ================= FETCH QUEUE ================= */
  const fetchQueue = async () => {
    try {
      const res = await api.get(`/api/queue/${serviceType}`);
      setQueue(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchQueue();
  }, [serviceType]);

  /* ================= ADD CUSTOMER ================= */
  const addCustomer = async () => {
    if (!name.trim()) return;

    await api.post("/api/queue/post", {
      customerName: name,
      serviceType,
    });

    setName("");
    fetchQueue();
  };

  /* ================= SERVE CUSTOMER ================= */
  const serveCustomer = async (id) => {
    await api.put(`/api/queue/serve/${id}`);
    fetchQueue();
  };

  // /* ================= SEARCH ================= */
   const filteredQueue = queue.filter((q) =>
     q.customerName.toLowerCase().includes(search.toLowerCase())
   );

   return (
     <>
       <Navbar />

       <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-200 dark:from-gray-900 dark:to-gray-800 flex justify-center pt-8">
         <div className="glass w-full max-w-xl rounded-2xl shadow-2xl p-6">

           {/* ================= HEADER ================= */}
           <div className="flex justify-between items-center mb-5">
             <h2 className="flex items-center gap-2 text-2xl font-bold text-indigo-700 dark:text-indigo-300">
               ‚ù§Ô∏è <span>Care Queue System</span>
             </h2>

             <button
              onClick={() => setDark(!dark)}
              className="px-3 py-1 rounded-full bg-indigo-600 text-white text-sm hover:scale-105 transition"
            >
              {dark ? "‚òÄ Light" : "üåô Dark"}
            </button>
          </div>

          //================= INPUT ROW =================
          <div className="flex gap-2 mb-4">
            <input
              className="flex-1 px-4 py-2 rounded-lg border dark:bg-gray-800 dark:text-white"
              placeholder="Customer Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />

            <select
              className="px-3 py-2 rounded-lg bg-indigo-50 dark:bg-gray-700 dark:text-white"
              value={serviceType}
              onChange={(e) => setServiceType(e.target.value)}
            >
              <option>GENERAL</option>
              <option>BILLING</option>
              <option>CONSULTATION</option>
            </select>

            <button
              onClick={addCustomer}
              className="btn-ripple px-5 py-2 rounded-lg bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-semibold hover:scale-105"
            >
              Add
            </button>
          </div>

          {/* ================= SEARCH ================= */}
          <input
            placeholder="üîç Search customer"
            className="w-full mb-4 px-4 py-2 rounded-lg border dark:bg-gray-800 dark:text-white"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          <h3 className="font-semibold text-indigo-600 dark:text-indigo-300 mb-3">
            {serviceType} Queue
          </h3>





          {/* ================= QUEUE LIST ================= */}
          <ul className="space-y-3 max-h-80 overflow-y-auto queue-scroll">
            {filteredQueue.length === 0 && (
              <p className="text-center text-gray-400 italic">
                No customers found
              </p>
            )}

            {filteredQueue.map((item, index) => (
              <li
                key={item.id}
                className="queue-card glass flex items-center justify-between px-5 py-4 rounded-xl shadow"
              >
                {/* LEFT */}
                <div className="flex items-center gap-4">
                  <span className="token-badge">
                    Q-{index + 101}
                  </span>

                  <div className="flex flex-col leading-tight">
                    <p className="text-sm font-semibold text-gray-900 dark:text-white">
                      {item.customerName}
                    </p>
                    <span className="text-xs text-gray-500 dark:text-gray-400">
                      {item.serviceType}
                    </span>
                  </div>
                </div>

                {/* RIGHT */}
                <button
                  onClick={() => serveCustomer(item.id)}
                  className="btn-ripple px-4 py-1.5 text-sm bg-green-600 text-white rounded-full hover:bg-green-700"
                >
                  Serve
                </button>
              </li>
            ))}
          </ul>
         </div>
       </div>
     </>
   );
}
