import { useEffect, useState } from "react";
import api from "../services/api";

export default function Queue() {
  const [name, setName] = useState("");
  const [serviceType, setServiceType] = useState("GENERAL");
  const [queue, setQueue] = useState([]);

  // Fetch queue from backend
  const fetchQueue = async () => {
    try {
      const res = await api.get(`/api/queue/${serviceType}`);
      setQueue(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchQueue();
  }, [serviceType]);

  // Add customer to queue
  const addCustomer = async () => {
    if (!name) return;
    try {
      await api.post("/api/queue/post", { customerName: name, serviceType });
      setName("");
      fetchQueue();
    } catch (err) {
      console.error(err);
    }
  };

  // Serve customer
  const serveCustomer = async (id) => {
    try {
      await api.put(`/api/queue/serve/${id}`);
      fetchQueue();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Queue Management</h2>

      <div style={styles.row}>
        <input
          style={styles.input}
          placeholder="Customer Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <select
          style={styles.select}
          value={serviceType}
          onChange={(e) => setServiceType(e.target.value)}
        >
          <option value="GENERAL">GENERAL</option>
          <option value="BILLING">BILLING</option>
          <option value="CONSULTATION">CONSULTATION</option>
        </select>

        <button style={styles.btn} onClick={addCustomer}>
          Add
        </button>
      </div>

      <hr />

      <h3>{serviceType} Queue</h3>
      <ul>
        {queue.map((item) => (
          <li key={item.id} style={styles.listItem}>
            {item.customerName}
            <button
              style={styles.serveBtn}
              onClick={() => serveCustomer(item.id)}
            >
              Serve
            </button>
          </li>
        ))}
        {queue.length === 0 && <p>No customers in queue</p>}
      </ul>
    </div>
  );
}

const styles = {
  container: { padding: "20px", maxWidth: 500, margin: "0 auto" },
  row: { display: "flex", gap: 10, marginBottom: 10 },
  input: { flex: 1, padding: 8 },
  select: { padding: 8 },
  btn: { padding: "8px 12px", background: "black", color: "white", border: "none", cursor: "pointer" },
  listItem: { display: "flex", justifyContent: "space-between", padding: "6px 0" },
  serveBtn: { padding: "4px 8px", background: "green", color: "white", border: "none", cursor: "pointer" },
};
