import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "40px",
        background: "linear-gradient(135deg, #eef2ff, #f5f3ff)",
      }}
    >
      {/* ===== HERO ===== */}
      <div style={{ textAlign: "center", marginBottom: "40px" }}>
        <h1 style={{ fontSize: "36px", color: "#4338ca" }}>
          üè• Care Hospital
        </h1>
        <p style={{ fontSize: "18px", color: "#374151", marginTop: "10px" }}>
          Smart Queue Management for Better Healthcare
        </p>

        <button
          onClick={() => navigate("/queue")}
          style={{
            marginTop: "20px",
            padding: "12px 25px",
            backgroundColor: "#4f46e5",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            fontSize: "16px",
            cursor: "pointer",
          }}
        >
          Go to Queue ‚Üí
        </button>
      </div>

      {/* ===== QUICK CARDS ===== */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
          gap: "20px",
          maxWidth: "900px",
          margin: "auto",
        }}
      >
        <Card
          title="üìã Queue"
          desc="Manage patient queues easily"
          onClick={() => navigate("/queue")}
        />
        <Card
          title="üë§ Profile"
          desc="View your account details"
          onClick={() => navigate("/profile")}
        />
        <Card
          title="‚Ñπ About"
          desc="Know about our hospital"
          onClick={() => navigate("/about")}
        />
      </div>
    </div>
  );
}

/* ===== REUSABLE CARD ===== */
function Card({ title, desc, onClick }) {
  return (
    <div
      onClick={onClick}
      style={{
        background: "#ffffff",
        padding: "20px",
        borderRadius: "12px",
        boxShadow: "0 10px 20px rgba(0,0,0,0.08)",
        cursor: "pointer",
        transition: "0.3s",
      }}
      onMouseEnter={(e) =>
        (e.currentTarget.style.transform = "translateY(-5px)")
      }
      onMouseLeave={(e) =>
        (e.currentTarget.style.transform = "translateY(0)")
      }
    >
      <h3 style={{ color: "#4338ca" }}>{title}</h3>
      <p style={{ marginTop: "8px", color: "#374151" }}>{desc}</p>
    </div>
  );
}
