import { useEffect, useState } from "react";
import api from "../services/api"; // tumhara axios instance

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await api.get("/api/user/me"); // backend endpoint
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) return <p style={{ padding: "20px" }}>Loading...</p>;
  if (!user) return <p style={{ padding: "20px" }}>No user data found</p>;

  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "40px",
        background: "#f0f4ff",
        display: "flex",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          maxWidth: "500px",
          width: "100%",
          background: "#fff",
          padding: "30px",
          borderRadius: "12px",
          boxShadow: "0 10px 20px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ color: "#4338ca", marginBottom: "20px" }}>ðŸ‘¤ Profile</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
      </div>
    </div>
  );
}
