import React from "react";

export default function Sidebar({ user, isOpen, toggleSidebar, activeLink }) {
  return (
    <div
      className={`fixed top-0 left-0 h-full w-64 bg-white dark:bg-gray-800 shadow-md flex flex-col p-6 transform transition-transform duration-300 ease-in-out z-50
        ${isOpen ? "translate-x-0" : "-translate-x-64"} md:translate-x-0`}
    >
      {/* ===== User Info ===== */}
      <div className="flex flex-col items-center mb-8">
        <img
          src={user?.avatar || "https://via.placeholder.com/80"}
          alt="User Avatar"
          className="w-20 h-20 rounded-full mb-3 border-2 border-indigo-500"
        />
        <h2 className="text-lg font-bold text-gray-800 dark:text-gray-200">
          {user?.name || "User Name"}
        </h2>
        <p className="text-sm text-gray-500 dark:text-gray-400">
          {user?.email || "user@example.com"}
        </p>
      </div>

      {/* ===== Navigation ===== */}
      <nav className="flex flex-col gap-3">
        {["Home", "Queue", "Contact", "About", "Profile"].map((link) => (
          <a
            key={link}
            href={`#${link.toLowerCase()}`}
            className={`px-4 py-2 rounded-lg transition ${
              activeLink === link
                ? "bg-indigo-100 dark:bg-indigo-600 text-indigo-800 dark:text-white font-semibold"
                : "hover:bg-indigo-100 dark:hover:bg-indigo-600 text-gray-700 dark:text-gray-200"
            }`}
          >
            {link}
          </a>
        ))}
      </nav>

      {/* Optional footer */}
      <div className="mt-auto text-center text-xs text-gray-400 dark:text-gray-500">
        © 2026 Care Queue System
      </div>

      {/* Close button on mobile */}
      <button
        onClick={toggleSidebar}
        className="absolute top-4 right-4 md:hidden text-gray-700 dark:text-gray-200 text-xl"
      >
        ✖
      </button>
    </div>
  );
}
