import React from "react";

export default function Navbar({ toggleSidebar, dark, setDark }) {
  const username = localStorage.getItem("username") || "User";
  const role = localStorage.getItem("role") || "ROLE";

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div className="flex justify-between items-center px-6 py-3 bg-indigo-700 text-white shadow-md">
      
      {/* ===== Left: Sidebar toggle + username ===== */}
      <div className="flex items-center gap-4">
        <button
          onClick={toggleSidebar}
          className="p-2 rounded-md hover:bg-indigo-600 transition"
        >
          â˜°
        </button>
        <span className="font-semibold">
          ðŸ‘‹ {username} ({role})
        </span>
      </div>

      {/* ===== Right: Dark/Light toggle + Logout ===== */}
      <div className="flex items-center gap-4">
        <button
          onClick={() => setDark(!dark)}
          className="px-3 py-1 rounded-full bg-indigo-600 hover:bg-indigo-500 transition"
        >
          {dark ? "â˜€ Light" : "ðŸŒ™ Dark"}
        </button>

        <button
          onClick={logout}
          className="px-3 py-1 rounded-md bg-red-600 hover:bg-red-500 transition"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
