import { useEffect, useState } from "react";
import api from "../services/api";
import "./Queue.css";
import Navbar from "../components/Navbar";

export default function Queue() {
  const [name, setName] = useState("");
  const [serviceType, setServiceType] = useState("GENERAL");
  const [queue, setQueue] = useState([]);

  const fetchQueue = async () => {
    try {
      const res = await api.get(`/api/queue/${serviceType}`);
      setQueue(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchQueue();
  }, [serviceType]);

  const addCustomer = async () => {
    if (!name) return;
    await api.post("/api/queue/post", {
      customerName: name,
      serviceType,
    });
    setName("");
    fetchQueue();
  };

  const serveCustomer = async (id) => {
    await api.put(`/api/queue/serve/${id}`);
    fetchQueue();
  };

  return (
    <div className="queue-container">
      <h2>Queue Management</h2>

      <div className="queue-row">
        <input
          className="queue-input"
          placeholder="Customer Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <select
          className="queue-select"
          value={serviceType}
          onChange={(e) => setServiceType(e.target.value)}
        >
          <option value="GENERAL">GENERAL</option>
          <option value="BILLING">BILLING</option>
          <option value="CONSULTATION">CONSULTATION</option>
        </select>

        <button className="queue-add-btn" onClick={addCustomer}>
          Add
        </button>
      </div>

      <hr />

      <h3>{serviceType} Queue</h3>

      {queue.length === 0 && (
        <p className="empty-text">No customers in queue</p>
      )}

      <ul className="queue-list">
        {queue.map((item) => (
          <li key={item.id} className="queue-item">
            {item.customerName}
            <button
              className="queue-serve-btn"
              onClick={() => serveCustomer(item.id)}
            >
              Serve
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
